using Akiled.HabboHotel.GameClients;using Akiled.HabboHotel.Guides;
using System.Collections.Generic;
using System.Text;

namespace Akiled.HabboHotel.Rooms.Chat.Commands.Cmd{    class ShowGuide : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            GuideManager guideManager = AkiledEnvironment.GetGame().GetGuideManager();            if (guideManager.GuidesCount <= 0)            {                Session.SendHugeNotif("Nessuna guida utilizza lo strumento Alpha");            }            else            {                StringBuilder stringBuilder = new StringBuilder();                stringBuilder.Append("Guide in servizio(" + guideManager.GuidesCount + "):\r\r");                foreach (KeyValuePair<int, bool> entry in guideManager.GuidesOnDuty)                {                    GameClient guide = AkiledEnvironment.GetGame().GetClientManager().GetClientByUserID(entry.Key);                    if (guide == null)                        continue;                    if (entry.Value)                        stringBuilder.Append("- " + guide.GetHabbo().Username + " (In Servizio)\r");                    else                        stringBuilder.Append("- " + guide.GetHabbo().Username + " (Disponibile)\r");                }                stringBuilder.Append("\r");                Session.SendHugeNotif(stringBuilder.ToString());            }        }    }}